export default class Input extends Component {
    constructor() {
        super()

        this.state = {
            weightInput: 0,
            bodyFatInput: 0
        }
        this.handleForm = this.handleForm.bind(this)
    }

    handleWeight(e) {
        this.setState({ weightInput: e})
        console.log(this.state.weightInput)


    }

    handleBodyFat(e) {
        this.setState({ bodyFatInput: e})
    }

    handleForm(e) {
        e.preventDefault()
        const weight = this.state.weightInput
        const bodyFat = this.state.bodyFatInput
        const sum = parseFloat() + bodyFat
        console.log(sum)
        return sum



    }





    render() {

        const desiredBodyFat = this.props.location.state
        return (
            <div>
                <form onSubmit={this.handleForm}>
                   Current Weight<input placeholder='Enter Current Weight' type="number" onChange={(e) => {this.handleWeight(e.target.value)}} />
                    Current Body Fat<input placeholder='Enter Current Body Fat' type="number" onChange={(e) => {this.handleBodyFat(e.target.value)}} />
                    <button>Submit</button>
                    <h1>{}</h1>
                </form>

            </div>
        )
    }
}
