import React, { Component } from 'react'
import axios from 'axios';


export default class Logs extends Component {

    constructor() {
        super()

        this.state = {
            weight_logs: 0,
            body_fat_logs: 0,
            isHidden: true
        }

        this.handleDelete = this.handleDelete.bind(this)
        this.handleEdit = this.handleEdit.bind(this)
        this.toggleHidden = this.toggleHidden.bind(this)
        this.hiddenTrue = this.hiddenTrue.bind(this)
        this.onSubmit = this.onSubmit.bind(this)



    }

    handleWeight(e){
        this.setState({ weight_logs: e})

    }

    handleBodyFat(e) {
        this.setState({body_fat_logs: e})
    }

    toggleHidden() {
        this.setState({ isHidden: false})
    }

    hiddenTrue() {
        this.setState({ isHidden: true})
    }



    handleEdit() {
        axios.put(`/results/${this.props.id}`, {
            weight_logs: this.state.weight_logs,
            body_fat_logs: this.state.body_fat_logs
        })
        .then((response) => {
            console.log(response);
          }, (error) => {
            console.log(error);
          });
    }

    onSubmit(e) {
        e.preventDefault()
        axios.put(`/results/edit/${this.props.id}`, {
            weight_logs: this.state.weight_logs,
            body_fat_logs: this.state.body_fat_logs

        }


        )
        .then((response) => {
            console.log(response);
          }, (error) => {
            console.log(error);
          });


    }



    handleDelete() {
        axios.delete(`/results/${this.props.id}`

        )
        .then((response) => {
            console.log(response);
          }, (error) => {
            console.log(error);
          });
    }







    render() {

        return (
            <div>
                <h4>{this.props.weight}</h4>
                <h4>{this.props.bodyFat}</h4>
                <h4>{this.props.date}</h4>

                <div>

                </div>


            </div>
        )
    }
}
