            weight_logs: 0,
            body_fat_logs: 0,
            isHidden: true
        }

        this.handleDelete = this.handleDelete.bind(this)
        this.handleEdit = this.handleEdit.bind(this)
        this.toggleHidden = this.toggleHidden.bind(this)



    }

    toggleHidden() {
        this.setState({ isHidden: false})
    }



    handleEdit() {
