import React, { Component } from 'react'
import axios from 'axios'

export default class Register extends Component {
    constructor() {
        super()

        this.state = {
            email: '',
            passwords: ''
        }
        this.handleRegistry = this.handleRegistry.bind(this)
    }
    //build out axio request to handle registry
    handleRegistry(e) {
        e.preventDefault()


        axios.post('/users/register', {
            email: this.state.email,
            pwd: this.state.passw
        })
        .then((response) => {
            console.log(response);
          }, (error) => {
            console.log(error);
          });
    }

    handleEmail(e) {

        this.setState({ email: e})
    }
    handlePassword(e) {
        this.setState({ pwd: e})

    }


    render() {


        return (
            <div>
                <div>
                    <h3>Register</h3>
                    <form onSubmit={this.handleRegistry}>
                        <input type="email"  placeholder='email...' required onChange={(e) => {this.handleEmail(e.target.value)}} />
                        <input type="password" placeholder='password' required onChange={(e) => {this.handlePassword(e.target.value)}} />
                        <button>Register</button>
                    </form>
                </div>

            </div>
        )
    }
}
