import React, { Component } from 'react'
import './input.css'

export default class Input extends Component {
    constructor() {
        super()

        this.state = {
            weightInput: 0,
            bodyFatInput: 0,
            result: ''

        }
        this.handleForm = this.handleForm.bind(this)
    }

    handleWeight(e) {
        this.setState({ weightInput: e})
        console.log(this.state.weightInput)


    }

    handleBodyFat(e) {
        this.setState({ bodyFatInput: e})
    }

    handleForm(e) {
        e.preventDefault()
        let weight = parseFloat(this.state.weightInput)
        let currentBodyFat = parseFloat(this.state.bodyFatInput)
        let goalBodyFat = parseFloat(this.props.location.state)

        function leanBodyMass() {
        const fatMass = weight * currentBodyFat
        const lbm = weight - fatMass
        return lbm

        }

        const lbm = leanBodyMass()

        function goalBodyWeight() {
        const goalWeight = Math.floor(lbm/(1-goalBodyFat))
        return goalWeight

        }

        const goalWeight = goalBodyWeight()

        // console.log(goalBodyWeight())

        function totalFatWeightLost() {
        const fatToLose = weight - goalWeight
        return fatToLose
        }

        const totalFatLost= totalFatWeightLost()
        // console.log(totalFatWeightLost())

        function timeToGoal() {

        let weeks = 0

        for (let i = weight; i >= goalWeight; i-= (i * 0.01)){
        weeks++

        }
        return weeks
        }

        const weeksToGoal = timeToGoal()


        // console.log(timeToGoal())

        function tdee() {
        const kg = weight * .45
        const bmr = kg * 20
        const thermicFood = bmr * .1
        const exerciseCal = 400
        const nonExerciseCal = 400
        const result = bmr + thermicFood + exerciseCal + nonExerciseCal
        return Math.floor(result - 500)

        }

        const totalCals = tdee()

        function bodyFatCalc() {
        return(`You need to eat ${totalCals} per day to reach your goal of ${Math.floor(goalBodyFat * 100)}%. It will take ${weeksToGoal} weeks to achieve your goal. You will weight ${goalWeight}lbs and have lost ${totalFatLost} lbs of fat if no muscle mass was lost or gained in the process. It's common to lose or gain some muscle when cutting calories depending on exercise experience. This result should be taken as best case scenario.`)


}
    this.setState({ result: bodyFatCalc()})

    }





    render() {
        return (
            <div className='input'>

                <div></div>
                <form onSubmit={this.handleForm}>
                   <input placeholder='Enter Current Weight' type="number" onChange={(e) => {this.handleWeight(e.target.value)}} />
                    Enter as a decimal (Ex. 35% body fat (35/100 = .35)<input placeholder='Enter Current Body Fat' type="text" onChange={(e) => {this.handleBodyFat(e.target.value)}} />
                    <button>Submit</button>
                </form>


                <h1>{this.state.result}</h1>






            </div>

        )
    }
}
