import React, { Component } from 'react'
import axios from 'axios';
import Edit from '../Edit';

export default class Logs extends Component {

    constructor() {
        super()

        this.state = {
            weight_logs: 0,
            body_fat_logs: 0,
            isHidden: true
        }

        this.handleDelete = this.handleDelete.bind(this)
        this.handleEdit = this.handleEdit.bind(this)
        this.toggleHidden = this.toggleHidden.bind(this)
        this.hiddenTrue = this.hiddenTrue.bind(this)



    }

    handleWeight(e){
        this.setState({ weight_logs: e})

    }

    handleBodyFat(e) {
        this.setState({body_fat_logs: e})
    }

    toggleHidden() {
        this.setState({ isHidden: false})
    }

    hiddenTrue() {
        this.setState({ isHidden: true})
    }



    handleEdit() {
        axios.put(`/results/${this.props.id}`, {
            weight_logs: this.state.weight_logs,
            body_fat_logs: this.state.body_fat_logs
        })
        .then((response) => {
            console.log(response);
          }, (error) => {
            console.log(error);
          });
    }



    handleDelete() {
        axios.delete(`/results/${this.props.id}`

        )
        .then((response) => {
            console.log(response);
          }, (error) => {
            console.log(error);
          });
    }







    render() {

        return (
            <div>
                <h4>{this.props.weight}</h4>
                <h4>{this.props.bodyFat}</h4>
                <h4>{this.props.date}</h4>


<button type="button" class="btn btn-primary" data-bs-toggle="modal" data-bs-target="#exampleModal">
  Edit
</button>


<div class="modal fade" id="exampleModal" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
  <div class="modal-dialog">
    <div class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title" id="exampleModalLabel">Modal title</h5>
        <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
      </div>
      <div class="modal-body">
      <input type="text" className="form-control" placeholder onChange={(e) => {this.handleWeight(e.target.value)}}/>
      <input type="text" className="form-control" value={this.state.body_fat_logs} onChange={(e) => {this.handleBodyFat(e.target.value)}}/>
      </div>
      <div class="modal-footer">
        <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Close</button>
        <button type="button" class="btn btn-primary">Save changes</button>
      </div>
    </div>
  </div>
</div>


                {/* <button  onClick= {this.toggleHidden}>Edit</button>
                {!this.state.isHidden && <Edit id = {this.props.id} hidden = {this.hiddenTrue} />} */}

                <button onClick= {this.handleDelete}>Delete</button>

            </div>
        )
    }
}
