import React, { Component } from 'react'
import axios from 'axios'

export default class Register extends Component {
    constructor() {
        super()

        this.state = {
            email: '',
            pwd: ''
        }
        this.handleSubmit = this.handleSubmit.bind(this)
    }
    //build out axio request to handle registry
    handleRegistry() {
        e.preventDefault()
        const {email, pwd} = this.state

        axios.post('/users/register', email, pwd)
    }

    handleEmail(e) {

        this.setState({ email: e})
    }
    handlePassword(e) {
        this.setState({ pwd: e})

    }


    render() {


        return (
            <div>
                <div>
                    <h3>Register</h3>
                    <form onSubmit={this.handleRegistry}>
                        <input type="email"  placeholder='email...' required onChange={(e) => {this.handleEmail(e.target.value)}} />
                        <input type="password" placeholder='password' required onChange={(e) => {this.handlePassword(e.target.value)}} />
                        <button>Register</button>
                    </form>
                </div>

            </div>
        )
    }
}
